sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	removeGanntFields();

    function removeGanntFields() {
        var leftPaneFields = String(current.left_pane_fields).split(',');
        var tooltipFields = String(current.tooltip_text_fields).split(',');
        var titleField = String(current.tooltip_title_field);
        var gFields = new GlideRecord('x_milte_timedetail_gantt_field');
        gFields.addQuery('snapshot_definition', current.sys_id);
        gFields.query();
        while (gFields.next()) {
            var fieldInUse = false;
            for (var i = 0; i < leftPaneFields.length; i++) {
                if (gFields.field_name == leftPaneFields[i]) {
                    fieldInUse = true;
                }
            }
            for (var o = 0; o < tooltipFields.length; o++) {
                if (gFields.field_name == tooltipFields[o]) {
                    fieldInUse = true;
                }
            }

            if (String(gFields.field_name) == titleField) {
                fieldInUse = true;
            }
            if (gFields.field == 'short_description') {
                gs.info('short_description field in use?: ' + fieldInUse);
            }
            if (!fieldInUse) {
                // Remove
                gFields.deleteRecord();
            }
        }
    }

})(current, previous);
