sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    removeTitleField();

    function removeTitleField() {
        var titleField = String(current.tooltip_title_field);
        var leftPaneFields = String(current.left_pane_fields).split(',');
        var tooltipFields = String(current.tooltip_text_fields).split(',');

        var gFields = new GlideRecord('x_milte_timedetail_gantt_field');
        gFields.addQuery('snapshot_definition', current.sys_id);
        gFields.addQuery('title_field', true);
        gFields.query();
        while (gFields.next()) {
            // If not current titleField
            if (gFields.field_name != titleField) {
                // And Not a Left Pane or tooltip field
                var isLeftPaneField = false;
                for (var i = 0; i < leftPaneFields.length; i++) {
                    if (leftPaneFields[i] == gFields.field_name) {
                        isLeftPaneField = true;
                    }
                }

                var isTooltipField = false;
                for (var t = 0; t < tooltipFields.length; t++) {
                    if (tooltipFields[t] == gFields.field_name) {
                        isTooltipField = true;
                    }
                }

                if (!isLeftPaneField && !isTooltipField) {
                    gFields.deleteRecord();
                }
                else {
                    gFields.title_field = false;
                    gFields.update();
                }
            }
        }
    }

})(current, previous);
