sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') {
        return;
    }
    var ga = new GlideAjax('x_milte_timedetail.TimelineUtil');
    ga.addParam("sysparm_name", "getSourceTable");
    ga.addParam("sysparm_table_name", g_form.getValue("target_table"));
    ga.getXML(setSourceTable);
    function setSourceTable(response) {
        var result = response.responseXML.documentElement.getAttribute("answer");
        // alert(result);
        if (result != null && result != '') {
            g_form.setValue('source_table', result);
        }
    }
}
