sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    //alert(g_form.getValue('source_table'));
    //alert(newValue);
    var ga = new GlideAjax('x_milte_timedetail.TimelineUtil');
    ga.addParam("sysparm_name", "getTableName");
    ga.addParam("sysparm_sid", g_form.getValue('source_table'));
    ga.getXML(setName);
    function setName(response) {
        var value = response.responseXML.documentElement.getAttribute("answer");
        //alert(value);
        var jsonObj = JSON.parse(value);
        var currentTableLabelArr = String(jsonObj.table_label).split(' ');
        var i = 0;
        while (i <= currentTableLabelArr[currentTableLabelArr.length]) {
            i++;
        }
        //var snapshotTableName = jsonObj.table_label + " Snapshot";
        var snapshotTableName = "";
        if (String(jsonObj.table_label).indexOf('Snapshot') == -1) {
            if (i == 0) {
                snapshotTableName = jsonObj.table_label + " Snapshot";
            }
            else {
                snapshotTableName = jsonObj.table_label + " Snapshot " + i;
            }
        }
        else {
            snapshotTableName = jsonObj.table_label + i;
        }
        //alert(snapshotTableName);
        // if (g_form.getValue('name') == '') {
        g_form.setValue('name', snapshotTableName);
        // }
    }
}
