sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') {
        return;
    }
    var ga = new GlideAjax('x_milte_timedetail.TimelineUtil');
    ga.addParam("sysparm_name", "tableExists");
    ga.addParam("sysparm_table_label", g_form.getValue("name"));
    ga.getXML(validateTable);
    function validateTable(response) {
        var result = response.responseXML.documentElement.getAttribute("answer");
        // alert(result);
        if (result == 'true') {
            g_form.showFieldMsg('name', 'Invalid - Table name is already in use', 'error');
            var name = String(g_form.getValue('name'));
            var newName = name;
            var nameArr = name.split(' ');
            var num = parseInt(nameArr[nameArr.length - 1]);
            //alert('newName: ' + newName);
            //alert('num: ' + num);
            //alert('typeof(num): ' + typeof (num));
            if (isInt(num)) {
                num++;
                var firstPart = "";
                for (var i = 0; i < nameArr.length - 1; i++) {
                    if (firstPart == "") {
                        firstPart += nameArr[i];
                    }
                    else {
                        firstPart += " " + nameArr[i];
                    }
                }
                newName = firstPart + " " + num;
            }
            else {
                newName += ' ' + 1;
            }
            g_form.setValue('name', newName);
        }
        else {
            g_form.showFieldMsg('name', 'Valid - Table name is not in use', 'Info');
        }
    }

    function isInt(n) {
        return Number(n) === n && n % 1 === 0;
    }
}
