sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if ((g_form.getValue('tooltip_text_fields') == '' || g_form.getValue('tooltip_text_fields').indexOf(newValue) == -1) && g_form.isNewRecord()) {
        g_form.setValue('tooltip_text_fields', newValue, newValue);
    }
    if ((g_form.getValue('left_pane_fields') == '' || g_form.getValue('left_pane_fields').indexOf(newValue) == -1) && g_form.isNewRecord()) {
        g_form.setValue('left_pane_fields', newValue, newValue);
    }
    if (g_form.getValue('span_text_fields') == '' && g_form.isNewRecord()) {
        g_form.setValue('span_text_fields', 'short_description', 'Short description');
    }

}
