sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    createTriggerFields();
    function createTriggerFields() {
        var tFields = [];
        tFields = String(current.trigger_fields).split(',');
        gs.info('trigger_fields: ' + tFields.toString());
        for (var i = 0; i < tFields.length; i++) {
            gs.info('tFields[i]: ' + tFields[i]);
            var gr = new GlideRecord('x_milte_timedetail_snapshot_trigger');
            gr.addQuery('snapshot_definition', current.sys_id);
            gr.addQuery('trigger_field.name', tFields[i]);
            var orQuery = gr.addQuery('trigger_field.name', current.target_table);
            if (!current.source_table.super_class.nil()) {
                orQuery.addOrCondition('trigger_field.name', current.source_table.super_class.name);
            }
            gr.query();
            if (gr.next()) {
                // Already exists as trigger field do nothing.
            }
            else {
                gr.initialize();
                gr.field_name = String(tFields[i]);
                gr.snapshot_definition = current.sys_id;
                gr.insert();
            }
        }
    }
})(current, previous);
