sys_script_include.script:
var SnapshotTrigger = Class.create();
SnapshotTrigger.prototype = {
    initialize: function () {
    },
    triggerHasChanged: function (sourceTable) {
        // Returns true when a snapshot trigger field has changed for the current record
        // gs.info("sourceTable: " + sourceTable);
        var hasChanged = false;
        var gr = new GlideRecord('x_milte_timedetail_snapshot_trigger');
        gr.addQuery('snapshot_definition.source_table.name', sourceTable);
        gr.addQuery('snapshot_definition.active', true);
        gr.query();
        // gs.info("Row Count: " + gr.getRowCount());
        while (gr.next()) {
            if (current[gr.trigger_field.element].changes()) {
                hasChanged = true;
            }
        }
        return hasChanged;
    },
    buildTriggers: function (sourceTable) {
        // Constructs JSON string of all snapshot Trigger fields that have changed in the current record
        var triggers = '{"changedFields":[';
        var gr = new GlideRecord('x_milte_timedetail_snapshot_trigger');
        gr.addQuery('snapshot_definition.source_table.name', sourceTable);
        gr.addQuery('snapshot_definition.active', true);
        gr.query();
        while (gr.next()) {
            var currentFieldName = String(gr.trigger_field.element);
            if (current[currentFieldName].changes()) {
                var currentFieldValue = current[currentFieldName];
                currentFieldValue = (new global.JSON()).encode(String(currentFieldValue));
                // gs.info("currentFieldValue: " + currentFieldValue);
                var previousFieldValue = previous[currentFieldName];
                previousFieldValue = (new global.JSON()).encode(String(previousFieldValue));
                //currentFieldValue = new global.JSON().encode(currentFieldValue);
                if (triggers == '{"changedFields":[') {
                    triggers += '{"FieldName":"' + currentFieldName + '","OldValue":' + previousFieldValue + ',"NewValue":' + currentFieldValue + '}';
                }
                else {
                    triggers += ',{"FieldName":"' + currentFieldName + '","OldValue":' + previousFieldValue + ',"NewValue":' + currentFieldValue + '}';
                }
            }
        }
        triggers += "]}";
        return triggers;
    },
    type: 'SnapshotTrigger'
};
