sys_script_include.script:
var GetSourceSnapshotTable = Class.create();
GetSourceSnapshotTable.prototype = {
    initialize: function () {
    },
    getRefQual: function () {
        var result = "name=" + current.snapshot_definition.source_table.name;

        if (this.hasSuperClass(String(current.snapshot_definition.source_table.sys_id))) {
            result += this.getSuperClass(String(current.snapshot_definition.source_table.super_class.sys_id));
        }
        result += "^elementNOT LIKEsys_^ORelementLIKEdomain"; 
        return result;
    },
    hasSuperClass: function(sys_id) {
        var result = false;
        var gr = new GlideRecord('sys_db_object');
        if (gr.get(sys_id)) {
            if (!gr.super_class.nil()) {
                result = true;
            }
        }
        return result;
    },
    getSuperClass: function(table_sid) {
        var result = "^ORname=";
        var gr = new GlideRecord("sys_db_object");
        if (gr.get(table_sid)) {
            result += String(gr.name);
            if (!gr.super_class.nil()) {
                result += getSuperClass(String(gr.super_class.sys_id));
            }
        }
        return result;
     },
    type: 'GetSourceSnapshotTable'
};
