sysevent_script_action.script:
createChoices();
function createChoices() {
    var snapshotTblObj = new x_milte_timedetail.CreateSnapshotTable();
    var newChoices = String(event.parm1);
    var parsed = new global.JSON().decode(newChoices);
    for (var choice in parsed) {
        //  var jsonString = "{";
        var name, label, value, element, sequence;
        for (var prop in parsed[choice]) {
            //if (jsonString == "{") {
            //    jsonString += '"' + prop + '":"' + parsed[choice][prop] + '"';
            //}
            //else {
            //    jsonString += ',"' + prop + '":"' + parsed[choice][prop] + '"';
            //}
            if (prop == "name") {
                name = parsed[choice][prop];
            }
            if (prop == "label") {
                label = parsed[choice][prop];
            }
            if (prop == "value") {
                value = parsed[choice][prop];
            }
            if (prop == "element") {
                element = parsed[choice][prop];
            }
            if (prop == "sequence") {
                sequence = parsed[choice][prop];
            }
        }
        //  jsonString += "}";
        //  gs.info("JSONString: " + jsonString);
        gs.info("createChoice params - name: " + name + " label: " + label + " value: " + value + " element: " + element + " sequence: " + sequence);
        snapshotTblObj.createChoice(name, label, value, element, sequence);
    }
}
