sysevent_script_action.script:
//This function will be automatically called when this rule is processed.
var tableName = String(event.parm2);
deleteSnapshotTable(tableName);

function deleteSnapshotTable(tableName) {
   // gs.info('event.parm2/tableName: ' + tableName);
   // gs.info('event.parm1: ' + event.parm1);
    if (!gs.nil(tableName)) {
        var snapshotTblObj = new x_milte_timedetail.DeleteSnapshotTable();
        snapshotTblObj.deleteSLA(tableName);
        snapshotTblObj.deleteLabels(tableName);
        snapshotTblObj.deleteChoices(tableName);
        snapshotTblObj.deleteModule(tableName);
        snapshotTblObj.deleteTable(event.parm1);
    }
}
